INSERT INTO CONFIGURATION VALUES(3,'sendmail.newuser.subject','Herzlich willkommen beim DJJV!');
INSERT INTO CONFIGURATION VALUES(4,'sendmail.newuser.body','Hallo %s,\u000d\u000adu wurdest von deinem Vereinsvertreter als neues Mitglied im DJJV angelegt. Dadurch erh\u00e4lst du Zugriff zu deinem Digitalem Mitgliedsausweises. Du kannst auf das Portal zugreifen, indem du auf <a href="https://www.djjv.de/jupa">JuPa</a> gehst.\u000d\u000a\u000d\u000aDeine Zugangsdaten lauten:\u000d\u000a Benutzername: %s\u000d\u000a Passwort: %s\u000d\u000a \u000d\u000aViele Gr\u00fc\u00dfe\u000d\u000aDein DJJV');
